mapper.select:
SELECT
count(1)
FROM
tb_shop ts


        ts.shop_name like CONCAT('%',#{ShopCondition.shopName},'%')


        AND ts.shop_categoery_id = #{ShopCondition.shopCategoery.shopCategoeryId}


        AND ts.area_id = #{ShopCondition.area.areaId}


        AND ts.owner_id = #{ShopCondition.owner.userId}

SELECT ts.shop_id as shop_id, ts.owner_id as owner_id, ts.area_id as area_id, ts.shop_categoery_id as shop_categoery_id, ts.shop_name as shop_name, ts.shop_desc as shop_desc, ts.shop_addr as shop_addr, ts.phone as phone, ts.shop_img as shop_img, ts.priority as priority, ts.create_time as create_time, ts.last_edit_time as last_edit_time, ts.enable_status as enable_status, ts.advice as advice FROM tb_shop ts
ts.shop_name like CONCAT('%',#{ShopCondition.shopName},'%')
AND ts.shop_categoery_id = #{ShopCondition.shopCategoery.shopCategoeryId}
AND ts.area_id = #{ShopCondition.area.areaId}
AND ts.owner_id = #{ShopCondition.owner.userId}
ORDER BY ts.priority DESC LIMIT #{indexNum},#{pageSize}

mapper.update:
UPDATE tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, priority=#{priority}, create_time=#{createTime}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, area_id=#{area.areaId}, shop_categoery_id=#{shopCategoery.shopCategoeryId}
WHERE shop_id=#{shopId}
